import { useState } from "react";
import Home from "./pages/home/Home";
import { Routes, Route } from "react-router-dom";

interface HistoryType {
    allClicks: any;
}

interface ButtonType {
    handleClick: MouseEventHandler<HTMLButtonElement>
    text: string
}

const History = (props : HistoryType) => {
    return (
        <>
        {props.allClicks.length === 0 ? "the app is used by pressing the buttons" : "button press history: " + props.allClicks.join(' ')}
        </>
    )
}

const Button = ({handleClick, text} : ButtonType) => {
    <button onClick={handleClick}>
        {text}
    </button>
}

function App() {
    const [left, setLeft] = useState(0)
    const [right, setRight] = useState(0)
    const [allClicks, setAll] = useState<Array<String>>([])
    const [total, setTotal] = useState(0)

    const handleLeftClick = () => {
        setAll(allClicks.concat('L'))
        const updatedLeft = left + 1
        setLeft(updatedLeft)
        setTotal(updatedLeft + right)
    }

    const handleRightClick = () => {
        setAll(allClicks.concat('R'))
        const updatedRight = right + 1
        setRight(updatedRight)
        setTotal(updatedRight + left)
    }



  return (
    <>
        {left}
        <button onClick={handleLeftClick}>left</button>
        <button onClick={handleRightClick}>right</button>
        {right}
        <p>{allClicks.join(' ')}</p>
        <History allClicks={allClicks} />
    </>
  );
}

export default App;
