import axios from 'axios'
const baseUrl = 'http://127.0.0.1:5000'

const getAll = library => {
    return axios.get(`${baseUrl}/${library}/getData`).then(response => response.data.data)
}

const createComment = (library, newObject) => {
    return axios.post(`${baseUrl}/${library}/addData`, newObject).then(response => response.data.data)
}

const updateComment = (id, newObject, library) => {
    return axios.put(`${baseUrl}/${library}/updateData/${id}`, newObject).then(response => response.data.data)
}

const deleteComment = (id, library) => {
    return axios.get(`${baseUrl}/${library}/deleteData/${id}`).then(response => response.data.data)
}

export default { getAll, createComment, updateComment, deleteComment}
